import React, { useEffect, useState } from "react";
import "../ComponentStyle/MovieListStyle.css";
// import { useParams } from "react-router-dom";
import Card from "./Card";
import InfiniteScroll from "react-infinite-scroll-component";

const MovieList = () => {
    const [ismovie, setMovie] = useState([]);
    const [page, setpage] = useState(1);
    const [totalMovies, setMovieResults] = useState(0);

    const UpdateNews = async() => {
        // life cycle method
        let url = `https://api.themoviedb.org/3/movie/popular?api_key=4eee0494b9dc4b3dd0c76812804c13c1&language=en-US`;
        let data = await fetch(url);
        let parsedData = await data.json();
        console.log(parsedData);
        setMovie(parsedData.ismovie);
        setMovieResults(parsedData.totalMovie);
    };

    useEffect(() => {
        UpdateNews();
    }, []);

    const fetchMoreData = async() => {
        setpage(page + 1);
        let url = `https://api.themoviedb.org/3/movie/popular?api_key=4eee0494b9dc4b3dd0c76812804c13c1&language=en-US`;
        // this.setState({ loding: true });
        let data = await fetch(url);
        let parsedData = await data.json();
        setMovie(ismovie.concat(parsedData.ismovie));
        setMovieResults(parsedData.totalMovies);
    };

    return ( <
        InfiniteScroll dataLength = { ismovie }
        next = { fetchMoreData }
        hasMore = { ismovie !== totalMovies } >
        <
        div className = "movie__list" >
        <
        div className = "list__cards" > { " " } {
            ismovie.map((movie) => ( <
                Card movie = { movie }
                />
            ))
        } { " " } <
        /div>{" "} <
        /div> <
        /InfiniteScroll>
    );
};

export default MovieList;
