import React, { useEffect, useState } from "react";
import "../ComponentStyle/MovieListStyle.css";
// import { useParams } from "react-router-dom";
import Card from "./Card";
import InfiniteScroll from "react-infinite-scroll-component";

const MovieList = () => {
    const [articles, setArticles] = useState([]);
    const [page, setpage] = useState(1);
    const [totalResults, settotalResults] = useState(0);

    const UpdateNews = async() => {
        // life cycle method
        let url = `https://api.themoviedb.org/3/movie/popular?api_key=4eee0494b9dc4b3dd0c76812804c13c1&language=en-US`;
        let data = await fetch(url);
        let parsedData = await data.json();
        console.log(parsedData);
        setArticles(parsedData.articles);
        settotalResults(parsedData.totalResults);
    };

    useEffect(() => {
        UpdateNews();
    }, []);

    const fetchMoreData = async() => {
        setpage(page + 1);
        let url = `https://api.themoviedb.org/3/movie/popular?api_key=4eee0494b9dc4b3dd0c76812804c13c1&language=en-US`;
        // this.setState({ loding: true });
        let data = await fetch(url);
        let parsedData = await data.json();
        setArticles(articles.concat(parsedData.articles));
        settotalResults(parsedData.totalResults);
    };

    return ( <
        >
        <
        InfiniteScroll dataLength = { articles.length }
        next = { fetchMoreData }
        hasMore = { articles.length !== totalResults } >
        <
        div className = "movie__list" >
        <
        h1 className = "list__title" > movie < /h1> <
        div className = "list__cards" > { " " } {
            MovieList.map((movie) => ( <
                Card movie = { movie }
                />
            ))
        } { " " } <
        /div>{" "} <
        /div> <
        /InfiniteScroll> <
        />
    );
};

export default MovieList;
