import React, { useEffect, useState } from "react";
import Skeleton, { SkeletonTheme } from "react-loading-skeleton";
import "../ComponentStyle/CardStyle.css";
import { Link } from "react-router-dom";

const Card = ({ movie }) => {
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {
        setTimeout(() => {
            setIsLoading(false);
        }, 1500);
    }, []);

    return ( <
        > { " " } {
            isLoading ? ( <
                div className = "cards" >
                <
                SkeletonTheme color = "#202020"
                highlightColor = "#444" >
                <
                Skeleton height = { 300 }
                duration = { 2 }
                />{" "} <
                /SkeletonTheme>{" "} <
                /div>
            ) : ( <
                Link to = { "/movies/popular" }
                style = {
                    { textDecoration: "none", color: "white" } } >
                <
                div className = "cards" >
                <
                img className = "cards__img"
                src = { `https://image.tmdb.org/t/p/original${
                movie ? movie.poster_path : ""}` }
                alt = { movie ? movie.original_title : "" } // solve
                />{" "} <
                div className = "cards__overlay" >
                <
                div className = "card__title" > { " " } { movie ? movie.original_title : "" } { " " } <
                /div>{" "} <
                div className = "card__runtime" > { " " } { movie ? movie.release_date : "" } { " " } <
                span className = "card__rating" > { " " } { movie ? movie.vote_average : "" } { " " } <
                i className = "fas fa-star" / >
                <
                /span>{" "} <
                /div>{" "} <
                div className = "card__description" > { " " } { movie ? movie.overview.slice(0, 118) + "..." : "" } { " " } <
                /div>{" "} <
                /div>{" "} <
                /div>{" "} <
                /Link>
            )
        } { " " } <
        />
    );
};

export default Card;
