import React, { useEffect, useState } from "react";
import "react-responsive-carousel/lib/styles/carousel.min.css";
import { Carousel } from "react-responsive-carousel";
import { Link } from "react-router-dom";
import "../ComponentStyle/HomeStyle.css";
import MovieList from "./MovieList";

const Home = () => {
    const [popularMovies, setPopularMovies] = useState([]);
    const [page, setPage] = useState(1);
    const [totalPages, setTotalPages] = useState(0);

    useEffect(() => {
        const fetchPopularMovies = async() => {
            const apiKey = "4eee0494b9dc4b3dd0c76812804c13c1";
            const url = `https://api.themoviedb.org/3/movie/popular?api_key=${apiKey}&language=en-US&page=${page}`;

            try {
                const response = await fetch(url);
                const data = await response.json();
                const { results, total_pages } = data;

                setPopularMovies((prevMovies) => [...prevMovies, ...results]);
                setTotalPages(total_pages);
            } catch (error) {
                console.error("Error fetching movies:", error);
            }
        };

        fetchPopularMovies();
    }, [page]);

    const handleNextPage = () => {
        if (page < totalPages) {
            setPage(page + 1);
        }
    };

    return ( <
        >
        <
        div className = "poster" >
        <
        Carousel showThumbs = { false }
        autoPlay = { true }
        transitionTime = { 3 }
        infiniteLoop = { true }
        showStatus = { false }
        onClickItem = { handleNextPage } // Trigger next page on carousel click
        >
        {
            popularMovies.map((movie) => ( <
                Link key = { movie.id }
                style = {
                    { textDecoration: "none", color: "white" } }
                to = { "/" } >
                <
                div className = "posterImage" >
                <
                img src = { `https://image.tmdb.org/t/p/original${movie.backdrop_path}` }
                alt = { movie.original_title }
                /> <
                /div> <
                div className = "posterImage__overlay" >
                <
                div className = "posterImage__title" > { movie.original_title } <
                /div> <
                div className = "posterImage__runtime" > { movie.release_date } { " " } <
                span className = "posterImage__rating" > { movie.vote_average } < i className = "fas fa-star" / >
                <
                /span> <
                /div> <
                div className = "posterImage__description" > { movie.overview } <
                /div> <
                /div> <
                /Link>
            ))
        } <
        /Carousel> <
        MovieList category = "popular" / >
        <
        /div> <
        />
    );
};

export default Home;
