import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import "../ComponentStyle/MovieDetailStyle.css";
import LoadingBar from "react-top-loading-bar";

const MovieDetails = () => {
    const [currentMovieDetail, setMovie] = useState();
    const { id } = useParams();
    const [setLoading] = useState(false); // Add loading state
    const [progress, setProgress] = useState(0); // Add progress state

    useEffect(() => {
        getData();
        window.scrollTo(0, 0);
    }, []);

    const getData = () => {
        setLoading(true); // Set loading to true before making the API call
        setProgress(30); // Set an initial progress value
        try {
            fetch(
                    `https://api.themoviedb.org/3/movie/${id}?api_key=4eee0494b9dc4b3dd0c76812804c13c1&language=en-US`
                )
                .then((res) => res.json())
                .then((data) => setMovie(data));
            setProgress(100); // Set the progress to 100 when the API call is successful
        } finally {
            setTimeout(() => {
                setLoading(false); // Set loading to false after a short delay
                setProgress(0); // Reset the progress value
            }, 500); // Adjust the delay duration if needed
        }
    };

    return ( <
        div >
        <
        LoadingBar progress = { progress }
        height = { 3 }
        color = "#f11946" / > { " " } <
        div className = "movie" >
        <
        div className = "movie__intro" >
        <
        img className = "movie__backdrop"
        src = { `https://image.tmdb.org/t/p/original${
              currentMovieDetail ? currentMovieDetail.backdrop_path : ""
            }` }
        alt = { currentMovieDetail ? currentMovieDetail.original_title : "Movie Backdrop" }
        />{" "} <
        /div>{" "} <
        div className = "movie__posterBox" > < /div>{" "} <
        div className = "movie__detail" > { /* ... (rest of the JSX code) */ } <
        /div>{" "} <
        div className = "movie__links" > { /* ... (rest of the JSX code) */ } <
        /div>{" "} <
        div className = "movie__genres" > { /* ... (rest of the JSX code) */ } <
        /div>{" "} <
        img className = "movie__poster"
        src = { `https://image.tmdb.org/t/p/original${
            currentMovieDetail ? currentMovieDetail.poster_path : ""
          }` }
        alt = { currentMovieDetail ? currentMovieDetail.original_title + " Poster" : "Movie Poster" }
        />{" "} <
        div className = "movie__heading" > Production companies < /div>{" "} <
        div className = "movie__production" > { /* ... (rest of the JSX code) */ } <
        /div>{" "} <
        /div>{" "} <
        /div>
    );
};

export default MovieDetails;
