    // }, [])

    useEffect(() => {
        getData();
    }, [type]);

    const getData = () => {
        fetch(
                `https://api.themoviedb.org/3/movie/${
        type ? type : "popular"
      }?api_key=4eee0494b9dc4b3dd0c76812804c13c1&language=en-US`
            )
            .then((res) => res.json())
            .then((data) => setMovieList(data.results));
